mapper.update:
UPDATE todo
SET
todoTitle = #{todoTitle}
, todoContent = #{todoContent}
, todoDate = #{todoDate}
, repeatableYN = #{repeatableYN}
, repeatUnit = #{repeatUnit}
, startDate = #{startDate}
, endDate = #{endDate}
, weekDay = #{weekDay}
, monthDay = #{monthDay}
, finish = #{finish}
WHERE userId = #{userId}
and todoIdx = #{todoIdx}

mapper.delete:
DELETE FROM todo
WHERE todoIdx = #{todoIdx}
and userId = #{userId}
