mapper.insert:
insert into `user`
(userKakaoID, userName, userImage, userEmail, provider)
values
(#{userKakaoID},
#{userName},
#{userImage},
#{userEmail},
#{provider}
);

mapper.update:
UPDATE `user`
    SET
    userName = #{userName}
    , userEmail = #{userEmail}

    WHERE userKakaoID = #{userKakaoID}
